import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CryptoJS from 'crypto-js'; // Import CryptoJS
import './App.css';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [receiver, setReceiver] = useState('');

  // Function to fetch messages for the specified receiver
  const fetchMessages = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/messages/receiver/${receiver}`);
      setMessages(res.data.messages);
    } catch (err) {
      console.error('Error fetching messages:', err);
    }
  };

  // Fetch messages when the receiver input changes
  useEffect(() => {
    if (receiver) {
      fetchMessages();
    }
  }, [receiver]);

  const handleDecryptMessage = (encryptedMessage) => {
    // Decrypt the message
    const decryptedBytes = CryptoJS.AES.decrypt(encryptedMessage, 'your-secret-key');
    const decryptedMessage = decryptedBytes.toString(CryptoJS.enc.Utf8);
    alert(`Decrypted Message: ${decryptedMessage}`); // Show in a pop-up
  };

  return (
    <div className="App">
      <h1>Secure Messaging - Receiver</h1>

      <label htmlFor="receiver">Receiver:</label>
      <input
        type="text"
        id="receiver"
        value={receiver}
        onChange={(e) => setReceiver(e.target.value)}
        placeholder="Enter receiver name"
      />

      <h2>Received Messages</h2>
      {messages.length > 0 ? (
        <ul>
          {messages.map((msg, index) => (
            <li key={index} onClick={() => handleDecryptMessage(msg.message)}>
              <strong>From:</strong> {msg.sender} <br />
              <strong>Message:</strong> [Click to decrypt]
            </li>
          ))}
        </ul>
      ) : (
        <p>No messages to display</p>
      )}
    </div>
  );
};

export default App;
