import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [receiver, setReceiver] = useState(''); // Receiver state

  // Function to fetch messages for the specified receiver
  const fetchMessages = async () => {
    if (!receiver) return; // Skip if no receiver specified
    try {
      const res = await axios.get(`http://localhost:5000/api/messages/receiver/${receiver}`);
      setMessages(res.data.messages);
    } catch (err) {
      console.error('Error fetching messages:', err);
    }
  };

  // Fetch messages when the receiver input changes
  useEffect(() => {
    const interval = setInterval(fetchMessages, 5000); // Fetch new messages every 5 seconds
    return () => clearInterval(interval); // Cleanup on unmount
  }, [receiver]);

  return (
    <div className="App">
      <h1>Secure Messaging - Receiver</h1>

      <label htmlFor="receiver">Receiver:</label>
      <input
        type="text"
        id="receiver"
        value={receiver}
        onChange={(e) => setReceiver(e.target.value)}
        placeholder="Enter receiver name"
      />

      <h2>Received Messages</h2>
      {messages.length > 0 ? (
        <ul>
          {messages.map((msg, index) => (
            <li key={index}>
              <strong>From:</strong> {msg.sender} <br />
              <strong>Message:</strong> {msg.message}
            </li>
          ))}
        </ul>
      ) : (
        <p>No messages to display</p>
      )}
    </div>
  );
};

export default App;
