import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [message, setMessage] = useState('');
  const [response, setResponse] = useState('');
  const [error, setError] = useState('');
  const [sender, setSender] = useState(''); // Add sender state
  const [receiver, setReceiver] = useState(''); // Add receiver state
  const [success, setSuccess] = useState(false); // State for success message

  // Function to handle the submission of the message
  const sendMessage = async (e) => {
    e.preventDefault();

    if (!message || !sender || !receiver) {
      setError('Please enter a message, sender, and receiver');
      setSuccess(false);
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/api/messages/send', {
        sender: sender,    // Send the sender's name
        receiver: receiver, // Send the receiver's name
        message: message,   // Send the message content
      });

      setResponse(res.data.message); // Adjusted to match response format
      setSuccess(true); // Indicate that the message has been sent successfully
      setError('');
      setMessage(''); // Clear input after sending
    } catch (err) {
      setError('Error sending message. Please check your network and server.');
      setSuccess(false);
      console.error('Error details:', err); // More detailed error logging
    }
  };

  return (
    <div className="App">
      <h1>Secure Messaging - Sender</h1>

      <form onSubmit={sendMessage}>
        <label htmlFor="sender">Sender:</label>
        <input
          type="text"
          id="sender"
          value={sender}
          onChange={(e) => setSender(e.target.value)}
          placeholder="Enter sender name"
        />

        <label htmlFor="receiver">Receiver:</label>
        <input
          type="text"
          id="receiver"
          value={receiver}
          onChange={(e) => setReceiver(e.target.value)}
          placeholder="Enter receiver name"
        />

        <label htmlFor="message">Enter your message:</label>
        <input
          type="text"
          id="message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Enter your message"
        />

        <button type="submit">Send Message</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>Message sent successfully!</p>} {/* Success message */}
      {response && <p>Server Response: {response}</p>}
    </div>
  );
};

export default App;
