import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import './index.css'; // Import your styles

const App = () => {
  const [message, setMessage] = useState('');
  const [response, setResponse] = useState('');
  const [error, setError] = useState('');
  const [sender, setSender] = useState('ABHIRAM'); // Example sender name
  const [receiver, setReceiver] = useState('TRIAL'); // Example receiver name

  const sendMessage = async (e) => {
    e.preventDefault();

    if (!message || !sender || !receiver) {
      setError('Please enter a message, sender, and receiver');
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/api/messages/send', {
        sender: sender,
        receiver: receiver,
        message: message,
      });

      setResponse(res.data.message);
      setError('');
      setMessage('');
    } catch (err) {
      setError('Error sending message');
      console.error(err);
    }
  };

  return (
    <div className="App">
      <h1>Secure Messaging - Sender</h1>
      <form onSubmit={sendMessage}>
        <label htmlFor="sender">Sender:</label>
        <input
          type="text"
          id="sender"
          value={sender}
          onChange={(e) => setSender(e.target.value)}
          placeholder="Enter sender name"
        />

        <label htmlFor="receiver">Receiver:</label>
        <input
          type="text"
          id="receiver"
          value={receiver}
          onChange={(e) => setReceiver(e.target.value)}
          placeholder="Enter receiver name"
        />

        <label htmlFor="message">Enter your message:</label>
        <input
          type="text"
          id="message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Enter your message"
        />
        
        <button type="submit">Send Message</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {response && <p>Server Response: {response}</p>}
    </div>
  );
};

export default App;
